import Head from 'next/head'
import { useEffect, useState } from 'react';

const theme = {
  light: {
    backgroundColor: '#fff',
    textColor: '#2e2f30',
  },
  dark: {
    backgroundColor: '#2e2f30',
    textColor: '#fff',
  },
};

const META = {
  URL: 'https://jeremybenaim.com/',
  TITLE: 'Jeremy Benaim – Full Stack Javascript Developer',
  DESCRIPTION: 'Jeremy Benaim – Full Stack Javascript Developer. UX / Product. Available for freelance',
};

const FAVICON_STATE = {
  DEFAULT: 'DEFAULT',
  IDLE: 'IDLE',
};

const Index = () => {
  const [favicon, setFavicon] = useState(FAVICON_STATE.DEFAULT);
  const [isPageVisible, setIsPageVisible] = useState(true);

  const onVisibilityChange = () => {
    setIsPageVisible(document.hidden);
  }

  useEffect(() => {
    setFavicon(isPageVisible ? FAVICON_STATE.DEFAULT : FAVICON_STATE.IDLE)
  }, [isPageVisible]);


  useEffect(() => {
    document.addEventListener('visibilitychange', onVisibilityChange, false);

    return () => {
      document.removeEventListener('visibilitychange', onVisibilityChange, false);
    };
  }, [])

  return (
    <>
      <Head>
        <meta charset="utf-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />

        <title>{META.TITLE}</title>
        <link rel="canonical" href={META.URL} />

        <meta name="description" content={META.DESCRIPTION} />

        <meta property="og:locale" content="en_US" />
        <meta property="og:type" content="website" />
        <meta property="og:url" content={META.URL} />
        <meta property="og:site_name" content={META.TITLE} />
        <meta property="og:title" content={META.TITLE} />
        <meta property="og:description" content={META.DESCRIPTION} />

        <meta name="twitter:site" content="jeremybenaim" />
        <meta name="twitter:creator" content="jeremybenaim" />
        <meta name="twitter:url" content={META.URL} />
        <meta name="twitter:title" content={META.TITLE} />
        <meta name="twitter:description" content={META.DESCRIPTION} />
      </Head>
      <article>
        <p>Hi, I'm Jeremy. I build stuff for the internet.</p>
        <p>I'm a <strong>Senior Full Stack Javascript developer</strong> since 2008. My job is to help companies around the world to build successful products and deliver great user experiences.</p>
        <p>I have considerable expertise in Front End development with <strong>react</strong>, <strong>redux</strong>, <strong>nodejs</strong> and <strong>react-native</strong> and have worked with a number of different frameworks and languages throughout my career.</p>
        <p>I've been exclusively working remotely since 2016 and have been a supporter of the remote community since then.</p>
        <p>Companies I've worked with:</p>
        <p><a href="https://jour.com/">Jour</a>, <a href="https://www.waldo.io/">Waldo.io</a>, <a href="https://hivyapp.com/">Hivy</a> (YC W17, acquired), <a href="https://www.efounders.com/">eFounders</a> and <a href="https://viadeo.com/">Viadeo</a>.</p>
        <p><mark>If you’d like to chat <a href="mailto:jeremybenaim@gmail.com">feel free to email me</a>.</mark></p>
      </article>
      <style jsx global>{`
        *, *:before, *:after {@
          box-sizing: inherit;
        }

        html, body {
          background: ${theme.light.backgroundColor};
          color: ${theme.light.textColor};
        }

        html {
          height: 100%;
          box-sizing: border-box;
        }

        body {
          font-family: -apple-system,BlinkMacSystemFont,'Segoe UI','Roboto','Oxygen','Ubuntu', 'Cantarell','Fira Sans','Droid Sans','Helvetica Neue',sans-serif;
          line-height: 1.4;
          font-size: 16px;
          font-weight: 400;
          margin: 0;
          direction: ltr;
          font-feature-settings: 'kern';
          text-rendering: optimizeLegibility;
          -webkit-text-size-adjust: none;
          -webkit-font-smoothing: antialiased;
          -moz-osx-font-smoothing: grayscale;
          scroll-behavior: smooth;
        }

        article {
          margin: 1.5rem 2rem;
          max-width: 700px;
        }

        @keyframes fadeIn{
          0% {
            transform: scale(.97);
            opacity: 0;
          }
          to {
            transform: scale(1);
            opacity: 1;
          }
        }

        @media only screen and (min-width:992px) {
          article {
            animation: fadeIn .6s;
            animation-delay: .2s;
            animation-fill-mode: both;
          }
        }

        @media (prefers-color-scheme: dark) {
          html, body {
            background: ${theme.dark.backgroundColor};
            color: ${theme.dark.textColor};
          }
        }
      `}</style>
    </>
  );
};

export default Index;
