const theme = {
  light: {
    backgroundColor: '#fff',
    titleColor: '#2e2f3060',
    subTitleColor: '#2e2f30',
    textColor: '#2e2f30',
  },
  dark: {
    backgroundColor: '#2e2f30',
    textColor: '#fff',
  },
};

const Index = () => (
  <>
    <article>
      <h2>Hi, I'm Jeremy. I build stuff for the internet.</h2>
      <p>I'm a <strong>Senior Full Stack Javascript developer</strong> since 2008. My job is to help companies around the world to build successful products and deliver great user experiences.</p>
      <p>I have considerable expertise in Front End development with <strong>react</strong>, <strong>redux</strong>, <strong>nodejs</strong>and <strong>react-native</strong> and have worked with a number of different frameworks and languages thorough my career.</p>
      <p>I've been exclusively working remotely since 2016 and have been a supporter of the remote community since then.</p>
      <p>Companies I've worked with: <a href="https://jour.com/">Jour</a>, <a href="https://www.waldo.io/">Waldo.io</a>, <a href="https://hivyapp.com/">Hivy</a> (YC W17, acquired by Managed by Q), <a href="https://www.efounders.com/">eFounders</a>, <a href="https://viadeo.com/">Viadeo</a>.</p>
      <p><mark>If youâ€™d like to chat <a href="mailto:jeremybenaim@gmail.com">feel free to email me</a>.</mark></p>
    </article>
  <style jsx global>{`
    *, *:before, *:after {
      box-sizing: inherit;
    }

    html, body {
      background-color: #fff;
      color: #2E2F30;
    }

    html {
      height: 100%;
      box-sizing: border-box;
    }

    body {
      font-family: -apple-system,BlinkMacSystemFont,'Segoe UI','Roboto','Oxygen','Ubuntu', 'Cantarell','Fira Sans','Droid Sans','Helvetica Neue',sans-serif;
      line-height: 1.65;
      font-size: 16px;
      font-weight: 400;
      margin: 0;
      direction: ltr;
      font-feature-settings: 'kern';
      text-rendering: optimizeLegibility;
      -webkit-text-size-adjust: none;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
      scroll-behavior: smooth;
    }
  `}</style>
  </>
);

export default Index;
